import React, { Component } from 'react';
import styled from 'styled-components';
import {
  Heading,
  Flex,
  Card,
  Text,
  Box,
  Button
} from 'rebass/styled-components'
import {
  Label,
  Input
} from '@rebass/forms'

const Page = styled.div`
  height: 100%;
  min-height: 100vh;
`;

class Demo extends Component {

  render() {
    return (
      <Page className="sweetmenu__page">
        <Flex
          flexDirection={['column', 'column', 'column', 'row']}
          alignItems="stretch"
          justifyContent="flex-start"
          height="100%"
          minHeight="100vh"
        >
          <Box
            p={[1,2,3]}
            bg="black"
            className="sweetmenu__aside">
            <Heading
              fontSize={[ 5, 5, 6 ]}
              color='primary'
              fontFamily="inherit"
              mb={3}
              className="sweetmenu__aside--title">
              Seeing is believing
            </Heading>
          </Box>
          <Box
            display="flex"
            flex="1"
            flexDirection="column"
            justifyContent="center"
            alignItems="stretch"
            p={[1,2,3]}
            className="sweetmenu__form">
            <Box
              mb={1}
              className="sweetmenu__form--inputGroup">
              <Label
                fontWeight="bold"
                htmlFor="fullName"
                className="sweetmenu__form--inputLabel">
                Full name
              </Label>
              <Input
                fontSize={1}
                bg="#e0e0e0"
                sx={{
                  borderRadius: "4px",
                  border: 0
                }}
                p={3}
                placeholder="Thomas Keller"
                type="text"
                name="fullName" />
            </Box>
            <Box
              mb={1}
              className="sweetmenu__form--inputGroup">
              <Label
                fontWeight="bold"
                htmlFor="email"
                className="sweetmenu__form--inputLabel">
                Email
              </Label>
              <Input
                fontSize={1}
                sx={{
                  borderRadius: "4px"
                }}
                p={3}
                placeholder="tkeller@thefrenchlaundry.com"
                type="email"
                name="email" />
            </Box>
            <Box
              mb={1}
              className="sweetmenu__form--inputGroup">
              <Label
                fontWeight="bold"
                htmlFor="restaurant"
                className="sweetmenu__form--inputLabel">
                Restaurant
              </Label>
              <Input
                fontSize={1}
                sx={{
                  borderRadius: "4px"
                }}
                p={3}
                placeholder="The French Laundry"
                type="text"
                name="restaurant" />
            </Box>
            <Box
              mb={1}
              className="sweetmenu__form--inputGroup">
              <Label
                fontWeight="bold"
                htmlFor="website"
                className="sweetmenu__form--inputLabel">
                Website
              </Label>
              <Input
                fontSize={1}
                sx={{
                  borderRadius: "4px"
                }}
                p={3}
                placeholder="tkeller@thefrenchlaundry.com"
                type="url"
                name="website" />
            </Box>
          </Box>
        </Flex>
      </Page>
    );
  }

}

export default Demo;
