import React, { Component } from 'react';
import styled from 'styled-components';
import {
  Heading,
  Flex,
  Card,
  Text
} from 'rebass/styled-components'
import {
  Label,
  Input
} from '@rebass/forms'

const Page = styled.div`
  background: black;
  height: 100%;
  min-height: 100vh;
`;

class Demo extends Component {

  render() {
    return (
      <Page className="sweetmenu__page">
        <Flex
          flexDirection="column"
          alignItems="center"
          justifyContent="flex-start"
          pt={3,3,4}
        >
          <Heading
            fontSize={[ 4, 5, 6 ]}
            color='primary'
            fontFamily="inherit"
            className="sweetmenu__form--title">
            Seeing is believing
          </Heading>
          <Card
            p={1}
            bg="primary"
            className="sweetmenu__form--card">
            <Text className="sweetmenu__form--cardCaption">
              Fill in your details below and we will get back to you ASAP with a free custom demo.
            </Text>
            <Box className="sweetmenu__form--inputGroup">
              <Label htmlFor="fullName" className="sweetmenu__form--inputLabel">
                Full name
              </Label>
              <Input name="fullName" />
            </Box>
          </Card>
        </Flex>
      </Page>
    );
  }

}

export default Demo;
