import React, { Component } from 'react';
import styled from 'styled-components';
import {
  Heading,
  Flex,
  Card,
  Text,
  Box
} from 'rebass/styled-components'
import {
  Label,
  Input
} from '@rebass/forms'

const Page = styled.div`
  background: black;
  height: 100%;
  min-height: 100vh;
`;

class Demo extends Component {

  render() {
    return (
      <Page className="sweetmenu__page">
        <Flex
          flexDirection="column"
          alignItems="center"
          justifyContent="flex-start"
          p={3,3,4}
        >
          <Heading
            fontSize={[ 5, 5, 6 ]}
            color='primary'
            fontFamily="inherit"
            mb={3}
            className="sweetmenu__form--title">
            Seeing is believing
          </Heading>
          <Card
            p={1}
            bg="primary"
            sx={{
              borderRadius: "4px"
            }}
            className="sweetmenu__form--card">
            <Text
              fontSize={2}
              mb={1}
              className="sweetmenu__form--cardCaption">
              Fill in your details below and we will get back to you ASAP with a free custom demo.
            </Text>
            <Box className="sweetmenu__form--inputGroup">
              <Label
                fontWeight="bold"
                htmlFor="fullName"
                className="sweetmenu__form--inputLabel">
                Full name
              </Label>
              <Input
                fontSize={1}
                sx={{
                  borderRadius: "4px"
                }}
                p={2}
                placeholder="Thomas Keller"
                name="fullName" />
            </Box>
          </Card>
        </Flex>
      </Page>
    );
  }

}

export default Demo;
