import React, { Component } from 'react';
import styled from 'styled-components';
import {
  Flex,
  Heading,
  Box,
  Card
} from 'rebass/styled-components'

const Page = styled.div`

`;

const Background = styled.div`
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-image: url('/static/sweetmenu-background.jpg');
  background-size: cover;
  background-position: top;
  z-index: -1
`;

class Index extends Component {

  render() {
    return (
      <Page className="sweetmenu">
        <Background />
        <Flex
          height="calc(100vh - 96px)"
          width="100vw"
          alignItems="center"
          justifyContent="center"
          className="sweetmenu__hero">
          <Heading
            fontSize={[ 5, 6, 6 ]}
            color='primary'
            fontFamily="inherit"
            className="sweetmenu__hero--title">
            Grow your <u>brunch spot</u>
          </Heading>
        </Flex>
        <Flex
          flexDirection={['column', 'column', 'row']}
        >
          <Box width={[1,1,1/2]} p={[1,2,3]}>
            <Card
              bg="#000"
              color="#fff"
              p="3rem"
              br="default"
            >
              <Heading
                fontSize={[ 4,5,5 ]}
                color='primary'
                fontFamily="inherit"
                className="sweetmenu__intro--title">
                Turn <u>browsers</u>
                <br/>
                into <u>diners</u>
              </Heading>
            </Card>
          </Box>
          <Box
            width={[1,1,1/2]}
            display="flex"
            alignItems="center"
            justifyContent="center"
            className="sweetmenu__demo">
            Demo
          </Box>
        </Flex>
      </Page>
    );
  }

}

export default Index;
