import React, { Component } from 'react';
import styled from 'styled-components';
import {
  Flex,
  Heading,
  Box,
  Card,
  Text
} from 'rebass/styled-components'
import Head from 'next/head'

const Page = styled.div`

`;

const Background = styled.div`
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-image: url('/static/sweetmenu-background.jpg');
  background-size: cover;
  background-position: top;
  z-index: -1
`;

const FeatureIcon = styled.i`
  width: 24px;
  height: 24px;
  border-radius: 50%;
  background: #fff;
  margin-right: .5rem;
  display: inline-flex;
  align-items: center;
  justify-content: center;

  & svg {
    scale: .75
  }
`;

class Index extends Component {

  render() {
    return (
      <Page className="sweetmenu">
        <Head>
          <title>Grow your brunch spot | Sweetmenu</title>
        </Head>
        <Background />
        <Flex
          height="calc(100vh - 96px)"
          width="100vw"
          alignItems="center"
          justifyContent="center"
          className="sweetmenu__hero">
          <Heading
            fontSize={[ 4, 5, 6 ]}
            color='primary'
            fontFamily="inherit"
            className="sweetmenu__hero--title">
            Grow your <u>brunch spot</u>
          </Heading>
        </Flex>
        <Flex
          flexDirection={['column', 'column', 'row']}
        >
          <Box width={[1,1,1/2]} p={[1,2,3]}>
            <Card
              bg="#000"
              color="#fff"
              p="3rem"
              sx={{
                boxShadow: '0 8px 16px -8px rgba(0,0,0,0.50)',
                borderRadius: 4
              }}
              className="sweetmenu__intro--card"
            >
              <Heading
                fontSize={[ 4,5,5 ]}
                mb={1}
                color='primary'
                fontFamily="inherit"
                className="sweetmenu__intro--title">
                Turn <u>browsers</u>
                <br/>
                into <u>diners</u>
              </Heading>
              <Text
                fontSize={[ 2,3,3 ]}
                mb={2}
                className="sweetmenu__intro--caption">
                Engage diners and grow your restaurant with just two lines of code.
              </Text>
              <Flex
                flexDirection="column"
                alignItems="flex-start"
                className="sweetmenu__intro--features">
                <Box
                  display="inline-flex"
                  flexDirection="row nowrap"
                  alignItems="center"
                  bg="#4a4a4a"
                  color="primary"
                  p=".5rem 1rem .5rem .5rem"
                  sx={{
                    borderRadius: "32px"
                  }}
                  className="sweetmenu__intro--feature">
                  <FeatureIcon className="sweetmenu__intro--featureIcon">
                    <svg width="24" height="24" viewBox="0 0 24 24">
                      <path d="M0 0h24v24H0z" fill="none"/>
                      <path d="M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z"/>
                    </svg>
                  </FeatureIcon>
                  Puts your menu front and center
                </Box>
              </Flex>
            </Card>
          </Box>
          <Box
            width={[1,1,1/2]}
            display="flex"
            alignItems="center"
            justifyContent="center"
            className="sweetmenu__demo">
            Demo
          </Box>
        </Flex>
      </Page>
    );
  }

}

export default Index;
